    val listData: ArrayList<Mosque>
        get() {
            val list = arrayListOf<Mosque>()
            for (position in mosqueName.indices) {
                val mesjid = Mosque(
                    mosqueName[position],
                    mosqueDescription[position],
                    mosqueAlamat[position],
                    linkMosque[position],
                    jumlahJamaah[position],
                    luasTanah[position],
                    tahunDididrikan[position],
                    mosquePhoto[position],
                    mosquePhotoDetail[position],
                )

                list.add(mesjid)
            }
            return list
        }
}